   * in error fallback rules.
   *
   * @param   errorCode  the code of the errormessage to display
   */


  /**
   * Pushes the specified amount of characters back into the input stream.
   *
   * They will be read again by then next call of the scanning method
   *
   * @param number  the number of characters to be read again.
   *                This number must not be greater than yylength()!
   */
  public fun yypushback(number: Int)  {
    if ( number > yylength() )
      zzScanError(ZZ_PUSHBACK_2BIG)

    zzMarkedPos -= number
  }


  private fun zzScanError(errorCode: Int) {
    val message = try {
      ZZ_ERROR_MSG[errorCode]
    } catch (e: IndexOutOfBoundsException) {
      ZZ_ERROR_MSG[ZZ_UNKNOWN_ERROR]
    }

    throw Error(message)
  }
  /**
   * Translates raw input code points to DFA table row
   */
  private fun zzCMap(input: Int): Int {
    val offset: Int = input and 255
    return if(offset == input) ZZ_CMAP_BLOCKS[offset] else ZZ_CMAP_BLOCKS[ZZ_CMAP_TOP[input shr 8] or offset]
  }

  /**
   * Resumes scanning until the next regular expression is matched,
   * the end of input is encountered or an I/O-Error occurs.
   *
   * @return      the next token
   */
  override fun advance(): SyntaxElementType?
  {
    var zzInput: Int = 0 
    var zzAction: Int = 0 

    val zzTransL: IntArray = ZZ_TRANS
    val zzRowMapL: IntArray = ZZ_ROWMAP
    val zzAttrL: IntArray = ZZ_ATTRIBUTE

    while (true) {
      // cached fields:
      var zzCurrentPosL = 0
      var zzMarkedPosL: Int = zzMarkedPos 
      var zzEndReadL: Int = zzEndRead 
      var zzBufferL: CharSequence = zzBuffer 

      zzAction = -1

      zzCurrentPosL = zzMarkedPosL
      zzCurrentPos = zzMarkedPosL
      zzStartRead = zzMarkedPosL

      zzState = ZZ_LEXSTATE[zzLexicalState]

      // set up zzAction for empty match case:
      var zzAttributes: Int = zzAttrL[zzState]
      if ( (zzAttributes and 1) == 1 ) {
        zzAction = zzState
      }


      zzForAction@ while (true) {
          if (zzCurrentPosL < zzEndReadL) {
            zzInput = zzBufferL.codePoint(zzCurrentPosL)
            zzCurrentPosL += charCount(zzInput)
          }
          else if (zzAtEOF) {
            zzInput = YYEOF
            break@zzForAction
          }
          else {
            // store back cached positions
            zzCurrentPos  = zzCurrentPosL
            zzMarkedPos   = zzMarkedPosL
            val eof: Boolean = zzRefill()
            // get translated positions and possibly new buffer
            zzCurrentPosL  = zzCurrentPos
            zzMarkedPosL   = zzMarkedPos
            zzBufferL      = zzBuffer
            zzEndReadL     = zzEndRead
            if (eof) {
              zzInput = YYEOF
              break@zzForAction
            }
            else {
              zzInput = zzBufferL.codePoint(zzCurrentPosL)
              zzCurrentPosL += charCount(zzInput)
            }
          }
          val zzNext: Int = zzTransL[zzRowMapL[zzState] + zzCMap(zzInput)]
          if (zzNext == -1) break@zzForAction
          zzState = zzNext

          zzAttributes = zzAttrL[zzState]
          if ( (zzAttributes and 1) == 1 ) {
            zzAction = zzState
            zzMarkedPosL = zzCurrentPosL
            if ( (zzAttributes and 8) == 8 ) break@zzForAction
          }
      }

      // store back cached position
      zzMarkedPos = zzMarkedPosL

      if (zzInput == YYEOF && zzStartRead == zzCurrentPos) {
        zzAtEOF = true
        return null
      }
      else {
        when (if (zzAction < 0) zzAction else ZZ_ACTION[zzAction]) {
          1 -> {
            lastBlockType = BlockType.Paragraph
            return SyntaxTokenTypes.BAD_CHARACTER
            }
          // fall through
          29 -> break
          2 -> {
            lastBlockType = BlockType.Paragraph
            yybeginAndUpdate(CONTENTS)
            return KDocTokens.TEXT
            }
          // fall through
          30 -> break
          3 -> {
            return SyntaxTokenTypes.WHITE_SPACE
            }
          // fall through
          31 -> break
          4 -> {
            yybeginAndUpdate(LINE_BEGINNING)
            return SyntaxTokenTypes.WHITE_SPACE
            }
          // fall through
          32 -> break
          5 -> {
            lastBlockType = BlockType.Paragraph
            yybeginAndUpdate(CONTENTS)
            return KDocTokens.KDOC_LPAR
            }
          // fall through
          33 -> break
          6 -> {
            lastBlockType = BlockType.Paragraph
            yybeginAndUpdate(CONTENTS)
            return KDocTokens.KDOC_RPAR
            }
          // fall through
          34 -> break
          7 -> {
            yybegin(CONTENTS_BEGINNING)
            return KDocTokens.LEADING_ASTERISK
            }
          // fall through
          35 -> break
          8 -> {
            val state = yystate()

            if (
                // Recognize indented code blocks if only the line starts with an asterisk(s)
                state == CONTENTS_BEGINNING &&

                // If there are more than 4 spaces at the beginning of the line or a tab char, we are trying to recognize indented code block
                (zzMarkedPos - zzStartRead >= 4 || zzBuffer[zzStartRead] == '\t' || zzBuffer[zzMarkedPos - 1] == '\t') &&

                // If the last block type is paragraph, more than 1 consecutive line break is required
                (lastBlockType != BlockType.Paragraph || consecutiveLineBreakCount >= 2)
            ) {
                yybegin(INDENTED_CODE_BLOCK)
                lastBlockType = BlockType.Code
                return KDocTokens.CODE_BLOCK_TEXT
            }

            yybegin(if (yystate() == CONTENTS_BEGINNING) CONTENTS_BEGINNING else CONTENTS)

            return KDocTokens.TEXT  // internal white space
            }
          // fall through
          36 -> break
          9 -> {
            codeFenceChar = zzBuffer[zzStartRead]
            codeFenceLength = countRepeating(codeFenceChar)
            yybeginAndUpdate(CODE_SPAN_CONTENTS)
            return KDocTokens.TEXT;
            }
          // fall through
          37 -> break
          10 -> {
            yybeginAndUpdate(CONTENTS)
            return KDocTokens.TEXT
            }
          // fall through
          38 -> break
          11 -> {
            consecutiveLineBreakCount++
            yybegin(LINE_BEGINNING)
            return SyntaxTokenTypes.WHITE_SPACE
            }
          // fall through
          39 -> break
          12 -> {
            yybeginAndUpdate(TAG_TEXT_BEGINNING)
            return KDocTokens.MARKDOWN_LINK
            }
          // fall through
          40 -> break
          13 -> {
            yybeginAndUpdate(if (yystate() == INDENTED_CODE_BLOCK) INDENTED_CODE_BLOCK else CODE_BLOCK)
            return KDocTokens.CODE_BLOCK_TEXT
            }
          // fall through
          41 -> break
          14 -> {
            return KDocTokens.CODE_BLOCK_TEXT
            }
          // fall through
          42 -> break
          15 -> {
            yybegin(if (yystate() == INDENTED_CODE_BLOCK) LINE_BEGINNING else CODE_BLOCK_LINE_BEGINNING)
            return SyntaxTokenTypes.WHITE_SPACE
            }
          // fall through
          43 -> break
          16 -> {
            yybegin(CODE_BLOCK_CONTENTS_BEGINNING)
            return KDocTokens.LEADING_ASTERISK
            }
          // fall through
          44 -> break
          17 -> {
            return KDocTokens.CODE_SPAN_TEXT
            }
          // fall through
          45 -> break
          18 -> {
            yybeginAndUpdate(CODE_SPAN_LINE_BEGINNING)
            return SyntaxTokenTypes.WHITE_SPACE
            }
          // fall through
          46 -> break
          19 -> {
            val ch = zzBuffer[zzStartRead]
            val length = countRepeating(ch)
            if (length == codeFenceLength && ch == codeFenceChar) {
                // Code span end
                codeFenceLength = -1
                codeFenceChar = 0.toChar()
                yybeginAndUpdate(CONTENTS)
                return KDocTokens.TEXT
            } else {
                return KDocTokens.CODE_SPAN_TEXT
            }
            }
          // fall through
          47 -> break
          20 -> {
            yybeginAndUpdate(CODE_SPAN_CONTENTS)
            return KDocTokens.LEADING_ASTERISK
            }
          // fall through
          48 -> break
          21 -> {
            return if (isLastToken) KDocTokens.END else KDocTokens.TEXT
            }
          // fall through
          49 -> break
          22 -> {
            lastBlockType = BlockType.Paragraph
            yybeginAndUpdate(CONTENTS)
            return KDocTokens.MARKDOWN_ESCAPED_CHAR
            }
          // fall through
          50 -> break
          23 -> {
            lastBlockType = BlockType.Paragraph
            val tag = KDocKnownTag.findByTagName(zzBuffer.subSequence(zzStartRead, zzMarkedPos))
            yybeginAndUpdate(if (tag != null && tag.isReferenceRequired) TAG_BEGINNING else TAG_TEXT_BEGINNING)
            return KDocTokens.TAG_NAME
            }
          // fall through
          51 -> break
          24 -> {
            // lookahead expression with fixed lookahead length
            zzMarkedPos = zzBufferL.offsetByCodePoints(zzMarkedPos, -1)
            val ch =  zzBuffer[zzStartRead]
            val length = countRepeating(ch)
            if (length == codeFenceLength && ch == codeFenceChar) {
                // Code fence end
                codeFenceChar = 0.toChar()
                codeFenceLength = -1
                yybeginAndUpdate(CONTENTS)
                return KDocTokens.TEXT
            } else {
                return KDocTokens.CODE_BLOCK_TEXT
            }
            }
          // fall through
          52 -> break
          25 -> {
            yybeginAndUpdate(CONTENTS_BEGINNING)
            return KDocTokens.START
            }
          // fall through
          53 -> break
          26 -> {
            yybeginAndUpdate(CONTENTS)
            return KDocTokens.MARKDOWN_LINK
            }
          // fall through
          54 -> break
          27 -> {
            // lookahead expression with fixed lookahead length
            zzMarkedPos = zzBufferL.offsetByCodePoints(zzMarkedPos, -1)
            lastBlockType = BlockType.Paragraph
            yybeginAndUpdate(CONTENTS)
            return KDocTokens.MARKDOWN_LINK
            }
          // fall through
          55 -> break
          28 -> {
            // lookahead expression with fixed lookahead length
            zzMarkedPos = zzBufferL.offsetByCodePoints(zzMarkedPos, -1)
            lastBlockType = BlockType.Code
            codeFenceChar = zzBuffer[zzStartRead]
            codeFenceLength = countRepeating(codeFenceChar)
            yybeginAndUpdate(CODE_BLOCK_LINE_BEGINNING)
            return KDocTokens.TEXT
            }
          // fall through
          56 -> break
          else ->
            zzScanError(ZZ_NO_MATCH)
        }
      }
    }
    return null
  }


}
