internal fun equalDistanceMapping(code: Int, start: Int, pattern: Int): Int {
    val diff = code - start

    val length = pattern and 0xff
    if (diff >= length) {
        return code
    }

    val distance = (pattern shr 8) and 0xf
    if (diff % distance != 0) {
        return code
    }

    val mapping = pattern shr 12
    return code + mapping
}

internal fun Int.uppercaseCodePoint(): Int {
