        return this - 32
    }
    if (this < 0x80) {
        return this
    }
    val index = binarySearchRange(rangeStart, this)
    return equalDistanceMapping(this, rangeStart[index], rangeLength[index])
}

internal fun Char.uppercaseCharImpl(): Char {
    return toInt().uppercaseCodePoint().toChar()
}
